sys_ui_action.script:
function fetch() {
    if (typeof g_form !== 'undefined') {
    var username = g_form.getValue('username');
    var displayname = g_form.getValue('displayname');
    var email = g_form.getValue('email');

    // Display the values in an alert to verify they're being read correctly
    alert("Username: " + username + "\nDisplay Name: " + displayname + "\nEmail: " + email);

    if (!username || !displayname || !email) {
        alert("Please fill in all fields.");
        return false; // Prevents proceeding to server-side script
    }

    // Add a confirmation dialog for the user
    var proceed = confirm("Do you want to create this user?");
    if (!proceed) {
        return false; // Prevents proceeding to server-side script
    }

    // Add parameters for server-side execution (this is a test and won't execute server-side)
    g_scratchpad.username = username;
    g_scratchpad.displayname = displayname;
    g_scratchpad.email = email;

    // Continue to server-side script (would return true in real use)
    return true;
}
}

(function executeRule(current, g_scratchpad) {
    try {
        // Retrieve form values from g_scratchpad
        var username = g_scratchpad.username;
        var displayname = g_scratchpad.displayname;
        var email = g_scratchpad.email;

        // Prepare the SCIM API payload
        var payload = {
            schemas: ["urn:ietf:params:scim:schemas:core:2.0:User"],
            userName: username,
            displayName: displayname,
            active: true,
            emails: [
                {
                    value: email,
                    type: "work",
                    primary: true
                }
            ]
        };

        // Configure the RESTMessageV2 object
        var restMessage = new sn_ws.RESTMessageV2();
        restMessage.setEndpoint('https://earth-apis.my.evidian.com/scim/v2/api/Users');
        restMessage.setHttpMethod('POST');
        restMessage.setRequestHeader('Content-Type', 'application/json');

        // Add token to the Authorization header
        var token = "Bearer YOUR_ACCESS_TOKEN"; // Replace with your actual token
        restMessage.setRequestHeader('Authorization', token);

        restMessage.setRequestBody(JSON.stringify(payload));

        // Execute the REST call
        var response = restMessage.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        // Log the result or show a message
        if (httpStatus === 201 || httpStatus === 200) {
            gs.addInfoMessage("User created successfully!");
        } else {
            gs.addErrorMessage("Failed to create user. HTTP Status: " + httpStatus + ". Response: " + responseBody);
        }
    } catch (e) {
        gs.addErrorMessage("Error creating user: " + e.message);
        gs.error("Error creating user: " + e.message);
    }
})(current, g_scratchpad);
