sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    // Get user data from the request body (form submission)
    var userData = request.body.data; // Assumes JSON body is sent in the request

    // Create a new REST message
    var restMessage = new sn_ws.RESTMessageV2();
    restMessage.setHttpMethod('POST');
    restMessage.setEndpoint('https://earth-apis.my.evidian.com/scim/v2/api/Users');

    // Set necessary request headers
    restMessage.setRequestHeader('Content-Type', 'application/json');
    restMessage.setRequestHeader('Authorization', 'Bearer eyJraWQiOiJyc2EyNTZfc2lnIiwiYWxnIjoiUlMyNTYifQ..pt39E49E7BqSKxKQ7v-bblrJHsNHxIbZJ0QMl_QqOqW5pcC47z-WH4dvUTzTnqiMQk7C7fhcAcqWp9XU26UIHHPPuxzDuUQiaszqrfjOTR6gL7PHcEqBuUaSjK8Z3bc7pYaJ1v_yuKczsUXn-it2TRsMZ-LhR_8K2oG4Y4u1kmygfF5dR8iFZbvIZ1eoD_O859uOrRGYCcixW1YlWzLV5_czZZheuCDLmsoiJAICppbE5qcObamqFUKSyQhYuJQLISp-7Sogyw21Nji3g43jGNJ9i1LovTOx3g0bQ7FPWvF1P9nixfMVFsUIF6vclAvZ5gTT_8a4Ck3GMNWn4o_mYQ'); // Replace with your actual token

    // Construct the payload as per SCIM API requirements
    var payload = {
        "schemas": ["urn:ietf:params:scim:schemas:core:2.0:User"],
        "userName": userData.username, // User's username from form
        "displayName": userData.displayname, // User's display name from form
        "active": true, // Set active as true (can be adjusted if needed)
        "emails": [
            {
                "value": userData.email, // User's email from form
                "type": "work", // You can adjust the type based on the context
                "primary": true // Mark the email as primary
            }
        ]
    };

    // Set the request body with the JSON payload
    restMessage.setRequestBody(JSON.stringify(payload));

    // Execute the REST request
    var restResponse = restMessage.execute();
    var responseBody = restResponse.getBody();
    var httpStatus = restResponse.getStatusCode();

    // Handle the response based on status code
    if (httpStatus == 201) {
        // Successfully created the user
        var responseJSON = JSON.parse(responseBody);
        // Optionally, process or store the response data as needed
        response.setStatus(201);
        response.setBody(JSON.stringify({status: 'success', message: 'User created successfully.', userId: responseJSON.id}));
    } else {
        // Something went wrong
        var errorResponse = {
            "status": "error",
            "message": responseBody
        };
        response.setStatus(httpStatus);
        response.setBody(JSON.stringify(errorResponse));
    }
})(request, response);
